package zhen.yuan;/** * @Author:zhenyuan * @Date:Created in 16:25 2018/8/12 */public class BinarySearch {    public static int binarySearch(int[] array, int l, int h, int aim){        while (l<h){            int mid = l+(h-l)/2;            if (array[mid]>aim){                h = mid-1;                return binarySearch(array,l,h,aim);            }            if (array[mid]<aim){                l = mid+1;                return binarySearch(array,l,h,aim);            }            if (array[mid]==aim){                return mid;            }        }        return aim == array[l]?l:-1;    }}