package zhen.yuan;import java.util.Scanner;/** * @Author:zhenyuan * @Date:Created in 16:21 2018/8/12 */public class GetMatrix {    public static int[][] getMatrix(){        Scanner sc=new Scanner(System.in);        String s = sc.nextLine();        String[] ss=s.split(",");        int row = Integer.parseInt(ss[0]);        int col = Integer.parseInt(ss[1]);        int[][] Matrix=new int[row][col];        for (int i=0;i<row;i++){            ss = sc.nextLine().split(",");            for (int j = 0; j< col; j++){                Matrix[i][j] = Integer.parseInt(ss[j]);            }        }        return Matrix;    }}