package zhen.yuan;/** * @Author:zhenyuan * @Date:Created in 11:38 2018/6/15 */public class MyPrint {    public static void printArray(int[] array,String explain){        System.out.print(explain+":[ ");        for (int i :                array) {            System.out.print(i+" ");        }        System.out.print("]");        System.out.println();    }    public static void print(String s){        System.out.print(s);    }    public static void println(String ... s){        for (String str:s){            System.out.print(str);        }        System.out.println();    }}