package zhen.yuan;/** * @Author:zhenyuan * @Date:Created in 20:35 2018/8/12 */public class TreeCase {    static class TreeNode {        int val = 0;        TreeNode left = null;        TreeNode right = null;        public TreeNode(int val) {            this.val = val;        }    }    public static void main(String[] args){        TreeNode node = new TreeNode(8);        TreeNode root = node;        node.right = new TreeNode(10);        node.left = new TreeNode(6);        TreeNode temp = node;        node = node.left;        node.left = new TreeNode(5);        node.right = new TreeNode(7);        node = temp.right;        node.right = new TreeNode(11);        node.left = new TreeNode(9);        System.out.println(root);    }}